UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вольно, |3-6($C). Вижу, ты вернулся из боя. Мне доложили, что ты хорошо показал себя в Хилсбраде.', `RequestItemsText_loc8`='Повезло добыть ингредиенты? Надо сказать, нынче эти места просто кишат подонками из Альянса... По мне, они уже зашли слишком далеко...', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=527;
