UPDATE `locales_quest` SET `OfferRewardText_loc8`=NULL, `RequestItemsText_loc8`='Король Вариан уделяет огромное внимание просвещению и всемерно заботится о том, чтобы с ценных книг вовремя делались списки, доступные горожанам. Все просто: принесите мне библиотечный свиток, и я выдам вам копию любой имеющейся книги.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=579;
