UPDATE `locales_quest` SET `OfferRewardText_loc8`='Да. Ты сослужил мне добрую службу. Когда мой дух освободится, я войду в Пустоту и обрету трех отличных слуг!\n\nТы – гордость своего клана, |3-6($R), и умилостивил меня.', `RequestItemsText_loc8`='Я жду черепов...', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=585;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Мне не хочется верить тебе, но увы, ты несомненно выполнил мое задание. Теперь, когда Анатек пал, гнев мой поостыл.\n\nТвоя сила приведет тебя к великим свершениям, $N. Но однажды... Мы встретимся в Пустоте, и снова померяемся силой.\n\nЯ буду ждать тебя.', `RequestItemsText_loc8`='Хотя моя собственная плоть постепенно облезает с черепа, я чую твой страх.\n\nХа! Ты никогда не справишься с моим заданием...', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=586;
