UPDATE `locales_quest` SET `OfferRewardText_loc8`='Занзил Отверженный??\n\nЕсли Йеннику и правда попал в руки этого тролля, могущественного и злого знахаря, то надежды почти нет. Почти. Не больше букашечки.\n\nНо малая надежда лучше, чем совсем никакой.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=588;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Молодец, $N. Эти осколки правильной формы, и я уже слышу их голос.\n\nПока тебя не было, я говорил с духами джунглей. Теперь я знаю, где находится Око Разума…\n\n...И кто его охраняет.', `RequestItemsText_loc8`='Ты принес осколки, $N? Без них нам не обойтись, даже когда удастся заполучить Око Разума.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=589;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Невероятно! Какое мастерство! \n\nА я-то думал, ты будешь легкой добычей, $N. Да, хорошенько ты меня отделал. \n\nВот обещанные деньги... правда, не столько, сколько я сказал сначала, но должен же я был немного приврать.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=590;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Тебе удалось добыть Око? Твою силу должно воспевать в легендах, $N!\n\nКроме того, добывая око ты одолел Май\'Зота и отправил его в Пустоту. Вот почему духи молчали о нем.\n\nХорошо, что он покинул мир плоти и крови, но боюсь, что теперь, в царстве духов он снова захватит власть.\n\nВпрочем, об этом будем беспокоиться в другой раз. А теперь я проведу ритуал, чтобы приспособить Око Разума для наших нужд...', `RequestItemsText_loc8`='Приветствую тебя, $N. Духи поют хвалу твоей храбрости, но о победе над Май\'Зотом они молчат.\n\nНесешь ли ты мне Око?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=591;
