UPDATE `locales_quest` SET `OfferRewardText_loc8`='Передай мои соболезнования Длинной Косе и его солдатам. Я искренне опечален вестью о трагедии на мосту Тандола и падении Дун Модра...$B$BНо знайте, что и Стромгард пал! Именно поэтому мы сейчас стоим у Опорного пункта. Впереди – битва за наш великий город.$B$BДолг и честь не позволяют мне покинуть нагорье до тех пор, пока над Стромгардом не взовьется гордое знамя нашего короля! Придется Длинной Косе обходиться своими силами...', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=634;
