UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ты приложил много стараний, чтобы пройти обряды Матери-Земли, $N.\n\nМы, духи предков – память о могучих тауренах, отдавшие жизни за великий город Громовой Утес. Итак, я передаю тебе почетную обязанность хранить его от бед.\n\nТы прошел Обряд мудрости, юный |3-6($C). Отныне ты можешь с гордостью войти в Громовой Утес.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=773;
