UPDATE `locales_quest` SET `OfferRewardText_loc8`='Прекрасно! Мы сварим отличную похлебку, а для проклятых дыбогривов потеря нескольких боевых вепрей послужит уроком.', `RequestItemsText_loc8`='То, что боевые вепри творят в наших землях, уже переходит всякие границы. Сумел ли ты приструнить этих зверей? А бока и рыла принес?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=780;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Это и в самом деле тревожные новости! Но, зная об этих коварных планах, мы сможем призвать своих сородичей из Деревни Кровавого Копыта и отразить атаку. Сегодня ты спас жизни многих, $N!', `RequestItemsText_loc8`='Ты чем-то обеспокоен, $N? Какую весть ты принес?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=781;
