UPDATE `locales_quest` SET `OfferRewardText_loc8`='Отлично. Еще один доброволец. В эти дни у нас много таких, как ты.\n\nНадеюсь, нам хватит бойцов.\n\nЗемлям людей угрожают внешние враги, потому большая часть наших сил направлена к рубежам. И из-за этого бандитские шайки вольготно чувствуют себя в наших землях.\n\nНам приходится воевать на много фронтов... Готовьтся к долгой войне, $N.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=783;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Твоя отвага скоро станет известна всем, |3-6($C)! Вести о твоей победе в крепости Тирагард, наверное, уже достигли Оргриммара.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=784;
