UPDATE `locales_quest` SET `OfferRewardText_loc8`='Он у тебя! Молодец!\n\nТеперь у нас есть возможность вытеснить приспешников Пылающего Клинка из Долины Испытаний. Но, боюсь, этот грот – не единственный их оплот в наших землях.\n\nТак что эта встреча с ними была не последней.', `RequestItemsText_loc8`='Ну что, $N, задание выполнено? Удалось ли тебе раздобыть медальон Пылающего Клинка?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=794;
