UPDATE `locales_quest` SET `OfferRewardText_loc8`='Спасибо, $N! Тебе удалось спасти Миншину! Дух моего брата избавлен от рабства!', `RequestItemsText_loc8`='Ну что, $N, череп моего брата у тебя? Он наконец свободен?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=808;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вот как? Ниру хочет, чтобы Демоново семя было уничтожено? Странно…\n\nЧто ж, хорошо. Если такова его воля, я расскажу тебе, как уничтожить семя.', `RequestItemsText_loc8`='Чего тебе надо, молокосос? Если ты новобранец, давай сюда письмо, а иначе проваливай.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=809;
