UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ты спас мне жизнь, $N! Спасибо тебе. \n\nПрошу тебя, возьми эту вещь. Денег за нее не выручишь, но, надеюсь, она пригодится тебе в странствиях. Судя по тому, что ты говоришь, мне она пригодится нескоро. Кор\'гхан подвергнет меня еще множеству своих ритуалов, пока я не сумею показать, на что способен. Снова охотиться на вепрей, собирать хвосты скорпидов... вздыхает', `RequestItemsText_loc8`='Я даже рад, что не могу сейчас вернуться в Сен\'джин. Все смеялись бы над моей слабостью и глупостью!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=812;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вот противоядие, $N. Отнеси его поскорее Ринагу, да смотри, осторожнее!', `RequestItemsText_loc8`='Не забывай, если ты на какую-то тварь охотишься, первым делом надо как следует изучить ее повадки! Это может спасти тебе жизнь.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=813;
