UPDATE `locales_quest` SET `OfferRewardText_loc8`='Я расскажу всем, что Иглогривы прогнаны со своих обжитых мест, и что их жилища надо сжечь. Теперь, когда племя Иглогривых изгнано из Дуротара, границы нашей новой родины станут значительно безопаснее.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=837;
