UPDATE `locales_quest` SET `OfferRewardText_loc8`='Молодец, малыш. Побольше таких подвигов, и у меня станет одной проблемой меньше. Хочется верить, что они решат перенести свой лагерь.\n\nОстается одно – вернуть украденные товары!', `RequestItemsText_loc8`='Что происходит? Чем могу служить? Отправить груз, или, может быть, инженер понадобился?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=887;
