UPDATE `locales_quest` SET `OfferRewardText_loc8`='Я-то думал, почему Древо-оракул так долго не связывается со мной... Сдается мне, что как только мы решаем одну проблему, немедленно возникает другая, еще более сложная.$b$bБоюсь, мне никогда не закончить мои труды с Тельдрассилом и мы так и не вернем себе бессмертие. $b$bТем не менее, ты славно $Gсправился:справилась; с заданием Древа-оракула, и за усердие тебе положена награда.', `RequestItemsText_loc8`='Хм-м... В тебе силен дух леса, |3-6($c). Что у тебя за дело к верховному друиду калдорай?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=940;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вы сажаете сердце в кадку, и оно быстро в нее зарывается!\n\nЧерез несколько секунд оно снова выкапывается из земли – уже очищенное. Оно тихонько пульсирует... и вам хочется взять его в руки.', `RequestItemsText_loc8`='Кадка полна земли, специально подготовленной Деналаном.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=941;
