UPDATE `locales_quest` SET `OfferRewardText_loc8`='Великолепно, $N!$B$BТеперь посмотрим, смогу ли я использовать камень, чтобы распознать силы, скрытые в окаменелостях.', `RequestItemsText_loc8`='При помощи камня Релу я смогу раскрыть тайны моей окаменелости, а также и окаменелости Сомнамбулера с Темного Берега, $N.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=943;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Так ты $Gобнаружил:обнаружила; клан Сумеречного Молота у Меча Властителя?$B$BПлохо.$B$BДревний бог, пронзенный мечом, давно мертв, но это не значит, что исчезли и остатки его могущества.$B$BДолжно быть, клан Сумеречного Молота пытается найти его силу.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=944;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Моя сестра была – где? Терилун любит бродить по всяким местам, но все-таки! Это же очень далеко, да и Меч Властителя – вовсе не безопасное место...$B$BНу, спасибо вам, $n. С вашей стороны было очень любезно сообщить мне, что с ней все в порядке.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=945;
