UPDATE `locales_quest` SET `OfferRewardText_loc8`='Большое вам спасибо, $N. Это отличные экземпляры!$B$BКстати, когда вы были у Скалистого водопада, заметили ли что-нибудь, что могло подтвердить опасения Дерев?$B$BДерева мудры, но я надеялся, что на сей раз они ошибаются.', `RequestItemsText_loc8`='Вы еще не собрали грибов для меня, $N? В пещеру ходили?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=947;
