UPDATE `locales_quest` SET `OfferRewardText_loc8`='Благодарю тебя, $n. Может быть, было бы лучше... если бы я сам это сделал. Но даже после прошедших тысячелетий у меня нет сил поднять руку на свою возлюбленную.$B$BПожалуйста, оставь меня наедине с моим горем...', `RequestItemsText_loc8`='С неизбывной печалью в сердце я последовал за Шан\'до Яростью Бури в долгий сон, и взял свою боль с собой в сны, и спал на протяжении тысячелетий.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=963;
