UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вы нашли манускрипт! Что за радостный день! Мы сделаем с него копии и распространим, и древнее знание Лоргалиса станет достоянием наших братьев и сестер!$B$BВы даже не понимаете, какую услугу нам оказали, $N. Но я надеюсь, что этот дар покажет вам всю глубину нашей признательности нашим союзникам.', `RequestItemsText_loc8`='Долог путь до Ясеневого леса, и задача ваша нелегка. Но если вы доставите мне манускрипт, $N... Велика будет ваша награда.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=971;
