UPDATE `locales_quest` SET `OfferRewardText_loc8`='Да, корреляция температур вулкана и горячих источников очевидна. Конечно, надо бы еще раз проверить правильность уравнений, однако создается впечатление, что расстояние и температура... Хмм...', `RequestItemsText_loc8`='Я бы и сам пошел, но уж больно боюсь элементалей Огня!', `ObjectiveText1_loc8`='Найдите самую горячую точку Вулкана Огненного Венца', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=974;
