UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ты принес пыль! Я сделаю из нее припарки – они должны унять лихорадку Релары.$B$BЕще раз спасибо тебе, $N. Без твоей помощи мое дитя несомненно умерло бы.', `RequestItemsText_loc8`='Ты уже принес звездную пыль, $N?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1034;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='О, $N, ты спас мою дочь! Я знаю, что сила лунного камня вернет моей дочери силы!$B$BМне никогда не отблагодарить тебя за все, что ты для меня сделал, но... пожалуйста, возьми вот это. После Релары это самое дорогое, что есть у меня.', `RequestItemsText_loc8`='Ты сразился с Чащобным Оракулом, $N? Если так, до дай мне, пожалуйста, лунный камень. Релара при последнем издыхании!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1035;
