UPDATE `locales_quest` SET `OfferRewardText_loc8`='Брат Антон по-прежнему проповедует идеи Алого ордена? О-ох...', `RequestItemsText_loc8`='Кто идет? Что вам от меня надо?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1052;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Хотя путь Света не приемлет убийств и кровопролитий, но в подобных случаях кровавая жертва служит благу королевства и всего мира.$B$BВаши деяния в монастыре помогли спасти множество невинных жизней. $N, я восхищаюсь вашей решительностью.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1053;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Славная работа, $N.$B$BЭти фурболги постоянно угрожают нам. По счастью, теперь они некоторое время будут сидеть тихо.', `RequestItemsText_loc8`='Из-за этой фурболгской заразы дни славы Ясеневого леса остались далеко в прошлом.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1054;
