UPDATE `locales_quest` SET `OfferRewardText_loc8`='Великолепно, $N! Славная работа.$B$BЭто заклинание вместе с НГ-5 даст идеальную дистанционную взрывчатку.$B$BУ нас получится прекрасный отвлекающий маневр, и ты прокрадешься и сопрешь документы и инженерные планы Торговой Компании.$B$BТогда они не смогут вырубать леса так быстро.', `RequestItemsText_loc8`='Хо-хо, ты уже обратно? У тебя оно есть? Заклинание, в смысле?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1077;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Великолепно, $N. Спасибо тебе огромное.$B$BМожет быть, и я когда-нибудь смогу помочь тебе.', `RequestItemsText_loc8`='И еще раз привет тебе, $N.$B$BТебе удалось побывать в той долине в Каменном Когте и добыть чешую?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1078;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Просто великолепно! Это сильный удар по Торговой Компании.$B$BМы сможем узнать уязвимые места их машин, а также использовать их разработки для наших собственных целей. Поистине, это великий день.', `RequestItemsText_loc8`='Тебе удалось раздобыть чертежи, $N?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1079;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Великолепно, $N! Каэла и часовые будут счастливы. При твоих умениях да моих творениях нас никто и ничто не остановит!', `RequestItemsText_loc8`='Ну и как дела, $N?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1080;
