UPDATE `locales_quest` SET `OfferRewardText_loc8`='Перед вами лежит израненное тело Винсента.\n\nСтраж смерти Адамант написал отчет Хадреку... Но что бы ни хотел добавить к его словам Винсент, он унес это с собой в могилу.\n\nВ мертвой руке зажата небольшая безделушка...', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1098;
