UPDATE `locales_quest` SET `OfferRewardText_loc8`='Отлично! Этот яд как раз подойдет! Спасибо, $N!\n\nТеперь надо его вскипятить, добавить скиппер-отомер и нега-катализатор и... Черт! Где мои записи?!', `RequestItemsText_loc8`='Привет-привет! Ну, где же яд?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1104;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Панцири! Спасибо, $N!\n\nОго, да они еще тверже, чем я думал! Вот примусь с ними работать – так, поди, целый ящик инструментов погну!', `RequestItemsText_loc8`='Мой брат разрабатывает новое топливо, и это меня немного беспокоит... Мне нужно как можно быстрее сделать новый топливный бак!\n\n$N, тебе удалось добыть черепашьи панцири?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1105;
