UPDATE `locales_quest` SET `OfferRewardText_loc8`='Спасибо, $N! Осталось размолоть их, добавить кое-какие ингредиенты, и все будет готово. Тогда, по крайней мере, болид не пострадает от своей собственной скорости.\n\nЕще бы он от какого-нибудь препятствия не пострадал...', `RequestItemsText_loc8`='Эй, где там мои плавники? Если не защитить машину от ее же собственной скорости, долго она тут не протянет!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1107;
