UPDATE `locales_quest` SET `OfferRewardText_loc8`='Превосходно! Этого нам и не хватало, $N.', `RequestItemsText_loc8`='Вижу, ты вернулся. Удалось добыть гуано летучих мышей?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1109;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ого, вы только посмотрите! Кое-что хорошее в этой горе металлолома еще есть. Наверняка я смогу продать это за кругленькую сумму.\n\nА... это что, оторванное ухо? Ой! Не хотел бы я быть на месте того пилота.\n\nСпасибо, $N. Вот ваша плата. И еще бесплатный совет...\n\nДелайте вид, что вы круче всех – и тогда вам всегда удастся нагреть руки на чужой беде.', `RequestItemsText_loc8`='$N, вы уже были на равнинах? Нашли какие-нибудь детали, которые я просил?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1110;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ах, да! Детали для Крейвела. Хорошо, что за ними пришли! Этот хлам валяется тут уже давно, я даже начал бояться, что он проржавеет!', `RequestItemsText_loc8`='Следующий! Так, ты отправитель или получатель?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1111;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ура, детали! Надо отнести их гномам. Но сначала...\n\nВынем оттуда кое-что...', `RequestItemsText_loc8`='Вы уже забрали части из Кабестана?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1112;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Сколько же на тебе кровищи, $N! Я вижу, ты неплохо потрудился!', `RequestItemsText_loc8`='Ты уже вернулся из Монастыря Алого ордена? Для этого восхитительного эксперимента мне понадобится много сердец!\n\nНе сомневаюсь, Вариматас и Темная Госпожа будут рады, избавившись от алого сброда.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1113;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вот они! Мои гидромеханизмы и усилительные болты! То, что нужно. Большое спасибо!', `RequestItemsText_loc8`='Принес мне что-нибудь? Здорово! Ну-ка, посмотрим...', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1114;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Хотите узнать про Назза Парового Котла? Вроде это гонщик с Мерцающей равнины?\n\nДа, я могу кое-что разузнать про него, но это будет нелегко. И не сразу.\n\nИ не за бесплатно.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1115;
