UPDATE `locales_quest` SET `OfferRewardText_loc8`='Итак, вы пришли, $N... Значит, вы готовы к путешествию через дальние земли?\n\nРаз так, юный |3-6($C), то слушайте...', `RequestItemsText_loc8`='Меня преследуют воспоминания о моих кошмарах, $N. Удалось ли тебе убить отступников и завладеть их кристаллами?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1130;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Убив Сталезуба, вы избавили Тысячу Игл от страшной напасти.\n\nИ это, $N, ваш урок на сегодня.', `RequestItemsText_loc8`='Вы убили злобного Сталезуба, $N?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1131;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Добрый день! Вы приплыли из Гавани Менетил по поручению мистера Флинта? О, мистер Флинт разбирается в людях! Если он послал вас сюда – значит, здесь ваше место.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1132;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вы здесь, чтобы предложить помощь Альянсу, $N? любой помощи. Хотя красота все еще живет в Ясеневом лесу... война добралась и до этих мест.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1133;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Благодарю тебя, $N. Мне не хотелось отправлять тебя убивать этих созданий, которые, возможно, не по своей воле помогали нашим врагам, однако это был мой долг.$B$BБыть может, друг мой, твое сердце оплакивает величавых виверн, которых тебе пришлось убить, однако пусть тебе принесет утешение то, что твои союзники больше не будут погибать от их яда.', `RequestItemsText_loc8`='Тебе удалось добыть ядовитые железы величавых виверн, $N? Мы не можем позволить оркам использовать яд этих тварей против нас!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1134;
