UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ты стоишь передо мной, словно посланник Мести, и я склоняюсь в знак признательности.\n\nЖирная корова мертва, и я, зная, что ее потомки не будут топтать землю Азерота, могу потратить силы на что-нибудь более разумное. Надеюсь, прикончив их, ты ограбил усадьбу?', `RequestItemsText_loc8`='Убей их и принеси мне головы!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1164;
