UPDATE `locales_quest` SET `OfferRewardText_loc8`='Отлично! Благодарю тебя, $N!$B$BТеперь надо подумать, как именно приготовить эти языки, чтобы наилучшим образом подчеркнуть их изысканный вкус.$B$BНаконец-то у меня есть блюдо, достойное завсегдатаев \"Синего отшельника\" в Штормграде.', `RequestItemsText_loc8`='Ну как? Удалось добыть необходимые мне языки?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1204;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Отлично, $N. Я уверен – это было нелегкое испытание, но я рад, что мучения кроколиска наконец-то прекратились.', `RequestItemsText_loc8`='Ты добыл зуб Зыбуна, $N? Удалось ли тебе прекратить страдания этого существа?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1205;
