UPDATE `locales_quest` SET `OfferRewardText_loc8`='Прекрасно! О, я загоню их перекупщикам за очень хорошую цену! \n\nСпасибо за работу, $N. Ты вполне надежный поставщик!', `RequestItemsText_loc8`='Ну что, принес корни, $N?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1221;
