UPDATE `locales_quest` SET `OfferRewardText_loc8`='А! Так вас послал мой дядя?$B$BНу что ж, я тут собирал для него рецепты и добился определенных успехов, но мне нужна помощь. $B$BНадеюсь, вы не боитесь ноги промочить?', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1260;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Эта подвеска была на солдате Штормграда??\n\nБлагодарю тебя, $N, за то, что раскрыл загадку смерти Марга. Но эта подвеска... Это новая загадка. И, возможно, она несет угрозу и Орде и Альянсу.', `RequestItemsText_loc8`='Ты выяснил, что случилось с Маргом?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1261;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Эта подвеска сделана удивительно тнокой и искусной работы, $N. у такое не нужно, но многие люди головы бы не пожалели за такую драгоценность.\n\nЯ изучу эту подвеску повнимательнее. Будем надеяться что в свое время мы все про нее узнаем.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1262;
