UPDATE `locales_quest` SET `OfferRewardText_loc8`='А, великолепно. Вероятно, Перрин не догадывался, какие знания скрывает в себе этот фолиант.\n\nМы больше не допустим хищения книги. Смотри: вот схема заклинания, объясняющая, как следует направлять энергию.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1473;
