UPDATE `locales_quest` SET `OfferRewardText_loc8`='Тебя послал Андромах? Очень хорошо. У меня для тебя важное задание...', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1477;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Хорошо, я вижу, Агерон нашел вас, $N. Давайте не будем тратить время на болтовню и церемонии, а сразу же перейдем к вашему обучению. Вам еще очень много надо узнать.', `RequestItemsText_loc8`='Я полагаю, ты уже собрал достаточно погибельника?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1478;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вау, это даже круче, чем я себе представлял - Купина Вечности! Я и не думал, что она такая огромная, но оно именно такая! Ты тоже видел на ней птиц?! Интересно, как они сделали, чтобы ветки так выглядели!$B$BСпасибо, что сводил меня в Дарнас, $N. Ты классный!', `RequestItemsText_loc8`='Разве мы уже там?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1479;
