UPDATE `locales_quest` SET `OfferRewardText_loc8`='Я думал, что начальство Торговой Компании – настоящие тираны, но потом я познакомился с Пароклапаном, он хуже их всех!\n\nХа! И все же это лучше, чем сидеть без работы... Или, хуже того, работать с этим Геренцо. Если ты хочешь мне помочь, дело для тебя найдется.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1483;
