UPDATE `locales_quest` SET `OfferRewardText_loc8`='Молодец, $N!\n\nДа, злобные фамильяры для воинов Пылающего Клинка – всего лишь ручные зверушки, однако же твой успех предсказывает тебе великие дела!', `RequestItemsText_loc8`='Ты слышал приказ, $N. Речь идет о безопасности Дуротара! Выполняй свою задачу – либо уходи с позором.\n\nПокажи свою доблесть, выбей людей из крепости Тирагард!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1499;
