UPDATE `locales_quest` SET `OfferRewardText_loc8`='Молодец. Надеюсь, во время твоего визита в логово чернокнижники Пылающего Клинка сильно пострадали?\n\nОни причинили немало вреда вождю, да и нам, чернокнижникам, тоже.', `RequestItemsText_loc8`='Ну что, $N, табличка Верги у тебя? Пока ты ее не добудешь, я с тобой и разговаривать не стану!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1501;
