UPDATE `locales_quest` SET `OfferRewardText_loc8`='Тьфу, гадость…\n\nОднако сил у меня прибавляется. Пожалуй, я теперь смогу уковылять отсюда... Однако похоже, что ногу мне придется отнять. И руку тоже.', `RequestItemsText_loc8`='Ууф-ф...', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1515;
