UPDATE `locales_quest` SET `OfferRewardText_loc8`='Да?\n\nМ-м-м, у меня такое чувство, что я заранее знаю твой вопрос: кому взбредет в голову жить в сердце Степей в ожидании нападения иглогривов и без защиты? У всех нас свои причины действовать так или иначе. У меня другой вопрос – что заставило простого |3-1($R) отправиться в сердце Степей разговаривать с женщиной, которую не заботит собственное благополучие?\n\nГотова поспорить, что мы оба знаем ответ на этот вопрос.', `RequestItemsText_loc8`='Тебе удалось добраться до гнезд?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1530;
