UPDATE `locales_quest` SET `OfferRewardText_loc8`='Картография! Картинки, далекие от реальности. Они мне не нужны. \n\nКниги – повествование, описание! В них и только в них следует искать правду. Я обменял бы все свои карты на одну книгу о возвышении некромантии или сагу о торжестве Темной Госпожи.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1801;
