UPDATE `locales_quest` SET `OfferRewardText_loc8`='Так-так-так! Что это у нас?\n\nНевероятно, $N. Я был уверен, что это невозможно, но вот я держу в руках фолиант Заговора.\n\nПозволь мне полистать его, пока ты не ушел.', `RequestItemsText_loc8`='Может быть, фолиант Заговора и упоминался в сказках, но даже в самых невероятных историях есть доля правды.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=1803;
