UPDATE `locales_quest` SET `OfferRewardText_loc8`='Лес оплакивает леди Сатру, но это надо было сделать.$B$BБлагодарю тебя, $N.', `RequestItemsText_loc8`='В великой печали даю я тебе это поручение, но у нас нет надежды исцелить леди Сатру.$B$BМы надеемся, что если принести серебряные паутинные железы в жертву Элуне, то она благословит Сатру, и та возродится с покоем в душе.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=2518;
