UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ты достоин награды за то, что вернул Керноби целым и невредимым, $N. Если твой дальнейший путь ведет в Гномреган, вот это поможет тебе расправиться с никчемным предателем, Мекжинером Термоштепселем.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=2904;
