UPDATE `locales_quest` SET `OfferRewardText_loc8`='Я рад, что последнее, что он видел в своей жизни, была твоя стопа, попирающая его преступную голову, |3-6($r)! Теперь, когда Термоштепсель мертв, наши планы по возвращению Гномрегана на шаг приблизились к осуществлению!', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=2929;
