UPDATE `locales_quest` SET `OfferRewardText_loc8`='А, тебя прислал Белгром! Ты как нельзя более вовремя, |3-6($R).\n\nЯ расскажу, что у нас тут творится, а ты уж решай, по зубам ли тебе это... Хотя если тебя прислал Белгром, значит, сомневаться в твоей доблести не приходится.', `RequestItemsText_loc8`='Ты добыл зуб Зыбуна, $N? Удалось ли тебе прекратить страдания этого существа?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=2981;
