UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ветер следует за тобой, по земле ты шагаешь твердо, а дух уже согрет огнем – я бы сказала, что пришло время познать чистоту воды.\n\nПуть твой был долог, и сила духа позволит тебе пройти еще дальше. Но первый шаг делать тебе.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=2985;
