UPDATE `locales_quest` SET `OfferRewardText_loc8`='Быстроветр и Острокоготь минуту назад вернулись вместе со своим детенышем, и теперь от него не отходят. Ох, как гляну на них, аж слеза прошибает...$B$BЯ даже не знаю, чем мы можем отблагодарить вас, $N. Грифоны Заоблачного Пика и их всадники навсегда запомнят ваше имя.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`='Откройте клетку Остроклюва', `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=2994;
