UPDATE `locales_quest` SET `OfferRewardText_loc8`='Спасибо за помощь, $N. Если бы не мы, гиппогрифы уже вымерли бы. Впрочем, тебе случалось бывать в Фераласе, и ты знаешь не понаслышке, как опустошили землю Гордунни.$B$BЧто ж, ты видишь, как я занят. Можешь идти.$B$BЕще раз спасибо, $N.', `RequestItemsText_loc8`='Еще одна посылочка от Кургль?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=3022;
