UPDATE `locales_quest` SET `OfferRewardText_loc8`='О, хорошо, что вы пришли. Касательно тех волчьих сердец, что вы мне ранее принесли – есть один момент, требующий вашего внимания.', `RequestItemsText_loc8`='Вы уже уничтожили Плеть Ночи, $N? От этого зависит наш успех в борьбе против Гнилошкуров!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=3221;
