UPDATE `locales_quest` SET `OfferRewardText_loc8`='Само правосудие сегодня направляло твою руку, $N. Да будет твое деяние предостережением для всех, кто осмелится угрожать нашему дому! \n\nТы заслужил эту награду, юный |3-6($C).', `RequestItemsText_loc8`='Остроклык больше не будет угрожать нам, если ты принесешь мне его голову, $N.\n\nМы, таурены, живем охотой, а самая захватывающая охота – та, где ты и сам можешь стать жертвой. Если ты преуспеешь в этом задании, то поймешь, о чем я говорю.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=3376;
