UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вы помещаете круглый камень в углубление и слышите негромкий щелчок. \n\nТеперь камень на положенном ему месте... Но что же делать дальше? \n\nПожалуй, стоит осмотреть алтарь...', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=3446;
